import React,{ useState,useEffect } from 'react';
import {useLocation, Link} from 'react-router-dom';
import axios from "axios";
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Card from "./Cards";
import TextField from '@material-ui/core/TextField';
import Button from "react-bootstrap/Button";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Cart from './Cart';
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`vertical-tabpanel-${index}`}
      aria-labelledby={`vertical-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `vertical-tab-${index}`,
    'aria-controls': `vertical-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
  root: {
    flexGrow: 1,
    // backgroundColor: theme.palette.background.paper,
    // display: 'flex',
    height: 224,
    margin: "100px auto"
  },
  tabs: {
    borderRight: `1px solid ${theme.palette.divider}`,
  },
}));

function Customer() {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const email_id = params.get('email');
  const username = params.get('username');
  

  const [temp1,setTemp1]=useState([])
  const [temp2,setTemp2]=useState([])
  const [temp3,setTemp3]=useState([])
  const [cart,setCart]=useState([])
  const classes = useStyles();
  const [value, setValue] = React.useState(0);
  const [isExpanded, setExpanded] = useState(false);
  const [date, setdate] = useState(new Date());
  useEffect(() => {
    if (temp1) {
      axios.get('/'+'customer'+'/dis_ban',{params: {date:date}})
        .then(res=>{setTemp1(res.data)})
        .catch(error=>console.log(error))
    }
    if (temp2) {
      axios.get('/'+'customer'+'/dis_cater',{params: {date:date}})
       .then(res=>{setTemp2(res.data)})
       .catch(error=>console.log(error))
   }
   if (temp3) {
      axios.get('/'+'customer'+'/dis_photo',{params: {date:date}})
        .then(res=>{setTemp3(res.data)})
        .catch(error=>console.log(error))
   }
  },[]);

  useEffect(() => {
   if (cart) {
      axios.get('/'+'customer'+'/cart_items',{params: {cust_email: email_id, cust_name:username}})
        .then(res=>{setCart(res.data)})
        .catch(error=>console.log(error))
    }
  });

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>
      {!isExpanded && <div >
          <Row>
          <Col></Col>
          <Col>
          <form className={classes.container} noValidate>
                <TextField
                        id="date"
                        
                        label="Pick Date:"
                        container="inline"
                        inputStyle={{ textAlign: 'center' }}
                        type="date"
                        onChange={(s)=>{
                          setdate(s.target.value);
                          console.log(s.target.value);
                        }                
                        }
                        className={classes.textField}
                        InputLabelProps={{
                          shrink: true,
                        }}
                      />
                      </form>
          </Col>
          <Col></Col>
                        
          </Row>    
                
      <Row>
          <Button
            style={{margin:"20px auto"}} 
            variant="outline-dark"
            onClick={() =>{
              setExpanded(true);
            }}
            >
          Search
        </Button>
      </Row>       
      
      </div>}
      {isExpanded && <Row>
      <Col>
        <Tabs
          orientation="vertical"
          variant="scrollable"
          value={value}
          onChange={handleChange}
          aria-label="Vertical tabs example"
          className={classes.tabs}
        >
          <Tab label="Banquet Hall"  {...a11yProps(0)} />
          <Tab label="Caterer"  {...a11yProps(1)} />
          <Tab label="Photographer"  {...a11yProps(2)} />
          <Tab label="Cart" {...a11yProps(3)} />
          {/* <Tab label="Item Five" {...a11yProps(4)} /> */}
        </Tabs>
      </Col>
      <Col>
      <TabPanel value={value} index={0}>
        
      {temp1.map((noteItem, index) => {
        return (

            <Card
              key={index}
              id={index}
              sid="1"
              cust_email={email_id}
              cust_name={username}
              email={noteItem.email}
              name={noteItem.name}
              company={noteItem.company}
              contact={noteItem.tel}
              cost={noteItem.cost}
              capacity={noteItem.capacity}
              content={noteItem.about}
              date={date}
            />
        );
      })}
      </TabPanel>
      <TabPanel value={value} index={1}>
      {temp2.map((noteItem, index) => {
        return (
            <Card
              key={index}
              id={index}
              sid="2"
              cust_email={email_id}
              cust_name={username}
              email={noteItem.email}
              name={noteItem.name}
              company={noteItem.company}
              contact={noteItem.tel}
              cuisine={noteItem.cuisines}
              cost={noteItem.cost}
              content={noteItem.about}
              date={date}
            />
        );
      })}
      </TabPanel>
      <TabPanel value={value} index={2}>
      {temp3.map((noteItem, index) => {
        return (
            <Card
              key={index}
              id={index}
              sid="3"
              cust_email={email_id}
              cust_name={username}
              email={noteItem.email}
              name={noteItem.name}
              company={noteItem.company}
              contact={noteItem.contact}
              cost={noteItem.cost}
              content={noteItem.about}
              date={date}
            />
        );
      })}
      </TabPanel>
      <TabPanel value={value} index={3}>
      {cart.map((noteItem, index) => {
        return (
            <Cart
              cid="0"
              type={noteItem.type}
              company={noteItem.company}
              contact={noteItem.contact}
              cost={noteItem.cost}
            />
        );
      })}
      <Link to={{ 
          pathname: "/checkout", 
          state: {email_id,username}
          }}>
          <Button 
          variant="outline-dark"
          onClick={ () => {
            console.log(email_id,username);
            // axios.get('/book',{params: {cust_email: email_id, cust_name:username}})
            // .then(res=>{console.log("successfully routed",res)})
            // .catch(error=>console.log(error))
          }}
          >Checkout</Button>
        </Link>
      
      </TabPanel>
      </Col>
      <Col></Col>
      </Row>}
    </div>
  );
}

export default Customer;


