import React, { useState } from "react";
import AddIcon from "@material-ui/icons/Add";
import Fab from "@material-ui/core/Fab";
import Zoom from "@material-ui/core/Zoom";
import axios from 'axios';

function CreateArea(props) {
  const [isExpanded, setExpanded] = useState(false);

  const [note, setNote] = useState({
    title: "",
    tel: "",
    cost: "",
    content: ""
  });

  function handleChange(event) {
    const { name, value } = event.target;

    setNote((prevNote) => {
      return {
        ...prevNote,
        [name]: value
      };
    });
  }

  function submitNote(event) {
    props.onAdd(note);
    // setNote({
    //   title: "",
    //   tel: "",
    //   cost: "",
    //   content: ""
    // });
    console.log(note);

    const url = 'http://localhost:5000/photographer';
    const reactData = [{ id: 1, name:' Tom'}, { id: 2, name:' Sarah'}];
    let sendData = () => {
    axios.post(url, reactData)
      .then(res => console.log('Data send is:'+res.data))
      .catch(err => console.log("error"+err.data))
    }
    sendData();

    event.preventDefault();
  }

  function expand() {
    setExpanded((prevState) => {
      return !prevState;
    });
  }

  return (
    <div>
      <form className="create-note">
        <div>
          {/* <h1>Hello</h1> */}
          <h1 onClick={expand} style={{ textAlign: "center",fontFamily: "Montserrat,sans serif" }}>
            {" "}
            Welcome!!
            {" "}
          </h1>
        </div>
        {isExpanded && (
          <h5 style={({ textAlign: "center",fontFamily: "Montserrat,sans serif" } )}>Edit your details below.</h5>
        )}
        {isExpanded && (
          <h5 style={{ textAlign: "center",color:"white",fontFamily: "Montserrat,sans serif" }}>Edit your details below.</h5>
        )}
        {/* <div></div> */}
        {isExpanded && (
          <input
            name="title"
            onChange={handleChange}
            value={note.title}
            placeholder="Your company name"
          />
        )}
        {isExpanded && (
          <input
            name="tel"
            onChange={handleChange}
            value={note.tel}
            placeholder="Enter your contact..."
          />
        )}
        {isExpanded && (
          <input
            name="cost"
            onChange={handleChange}
            value={note.cost}
            placeholder="Per hour charge..."
          />
        )}
        {isExpanded && (
          <textarea
            name="content"
            onChange={handleChange}
            value={note.content}
            placeholder="About your service..."
            rows={isExpanded ? 3 : 1}
          />
        )}
        <Zoom in={isExpanded}>
          <Fab onClick={submitNote}>
            <AddIcon onClick={expand} />
          </Fab>
        </Zoom>
      </form>
    </div>
  );
}

export default CreateArea;
