import React,{useState,useEffect} from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import {useLocation} from 'react-router-dom';
import Button from "react-bootstrap/Button";
import Card from './Cards';
import axios from 'axios';

function Checkout(props){
    console.log(props.location);
    console.log(props.location.state);
    const location = useLocation();
    const params = new URLSearchParams(location.search);
    const email_id = params.get('email');
    const username = params.get('username');
    const [total,setTotal]=useState(0)
    const [cart,setCart]=useState([])
    useEffect(() => {
    if (cart) {
        axios.get('/'+'customer'+'/cart_items',{params: {cust_email: email_id, cust_name:username}})
            .then(res=>{setCart(res.data);calTotal(res.data)})
            .catch(error=>console.log(error))
        }
    },[]);
    function calTotal(cart){
        cart.map((item,index) =>{
            setTotal(total+item.cost);
        })
    }
    return (
    <Container>
        <Row>
            <Col>
                <h1>Total: {total}</h1>
            </Col>
        </Row>
        {JSON.stringify(cart)}
        {cart.map((noteItem, index) => {
        return (
            <Card
              key={index}
              id={index}
              sid={noteItem.sid}
              company={noteItem.company}
              contact={noteItem.tel}
              cost={noteItem.charge}
              content={noteItem.about}
            />
        );
      })}
      <Button 
      variant="outline-dark">Proceed to payment!</Button>

    </Container>
    );
}

export default Checkout;
